import axios from "axios"

const BASE_URL = 'https://api.spotify.com/v1';

export function fetchUser(token) {
    return axios.get(`${BASE_URL}/me`, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
  }

  export function fetchTopSongs(token, limit = 10, timeRange = 'short_term') {
    return axios.get(`${BASE_URL}/me/top/tracks?limit=${limit}&time_range=${timeRange}`, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
  }
