import React from "react"
import "../styles/Login.css"

const userTopRead = "user-top-read"

export default function LoginButton({ endpoint, clientId, redirectUri, responseType }) {
  const handleLogin = () => {
    window.location.href = `${endpoint}?client_id=${clientId}&redirect_uri=${redirectUri}&scope=${userTopRead}&response_type=${responseType}`
  }

  return (
    <button className="login-button" click={handleLogin} aria-label="Log in with Spotify">
      Log in with Spotify
    </button>
  )
}
