export function fetchUser(token) {
    return axios.get(`${BASE_URL}/me`, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })
      .then(response => {
        return response.data;
      })
      .catch(error => {
        console.error('Error fetching user:', error);
        throw error;
      });
  }

  export function fetchTopSongs(token, limit = 10, timeRange = 'short_term') {
    return axios.get(`${BASE_URL}/me/top/tracks?limit=${limit}&time_range=${timeRange}`, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })
      .then(response => {
        return response;
      })
      .catch(error => {
        console.error('Error fetching top songs:', error);
        throw error;
      });
  }
