import React from "react";
import ButtonGroup from "./ButtonGroup";
import "../styles/ImageGrid.css"

function getAlbumArtWork(songs) {
    return songs.map(song => song.album.images[1]);
}

const ImageGrid = ({ title, sogns , buttonHandler}) => {
    const buttons = [
        { label: "short term" },
        { label: "medium term" },
        { label: "long term" },
    ];

    function handleButtonClick(button) {
        buttonHandler(button)
    }

    let images = getAlbumArtWork(songs);

    return (
        <div>
            <h5 className="grid-view-title">{title}</h5>
            <div className="button">
                <ButtonGroup buttons={buttons} onButtonClick={handleButtonClick} />
            </div>
            <div className="image-grid">
            {images.map((image, index) => (
                <div className="image" key={index}>
                    <img src={image.url} alt={image.url} />
                    <div className="image-text">{"hello world!"}</div>
                </div>
            ))}
        </div>
        </div>
    );
};

export default ImageGrid;
