import React from "react"
import PropTypes from "prop-types"
import "../styles/Login.css"

const user_top_read = "user-top-read"

const Login = ({ authEndpoint, clientId, redirectUri, responseType }) => {
  const loginUrl = `${authEndpoint}?client_id=${clientId}&redirect_uri=${redirectUri}&scope=${user_top_read}&response_type=${responseType}`

  const handleLogin = (event) => {
    event.preventDefault()
    window.location.href = loginUrl
  }

  return (
    <button className="login-button" onClick={handleLogin}>
      Login
    </button>
  )
}

Login.propTypes = {
  authEndpoint: PropTypes.string.isRequired,
  clientId: PropTypes.string.isRequired,
  redirectUri: PropTypes.string.isRequired,
  responseType: PropTypes.string.isRequired,
}

export default Login
