import React from "react"
import PropTypes from "prop-types"
import { USER_TOP_READ, AUTH_ENDPOINT, CLIENT_ID, REDIRECT_URI, RESPONSE_TYPE} from "../consts"
import "../styles/Login.css"

const  LoginButton = ({}) => {
  const loginUrl = `${AUTH_ENDPOINT}?client_id=${CLIENT_ID}&redirect_uri=${REDIRECT_URI}&scope=${USER_TOP_READ}&response_type=${RESPONSE_TYPE}`

  const handleLogin = (event) => {
    event.preventDefault()
    window.location.href = loginUrl
  }

  return (
    <button className="login-button" onClick={handleLogin}>
      Login
    </button>
  )
}

LoginButton.propTypes = {
  authEndpoint: PropTypes.string.isRequired,
  clientId: PropTypes.string.isRequired,
  redirectUri: PropTypes.string.isRequired,
  responseType: PropTypes.string.isRequired,
}

export default LoginButton
